var express=require("express"),app=express(),fs=require("fs"),path=require("path");app.use(express.static(__dirname+"/public"));app.get("/",function(e,t){t.send(fs.readFileSync("./index.html",{encoding:"UTF-8"}))});app.configure("development",function(){app.set("content_url","/")});app.configure("production",function(){app.set("content_url","/public")});var port=process.env.PORT||8080;app.listen(port,function(){console.log("Listening on "+port)});